import React from 'react';
import { Activity, Thermometer, Gauge, AlertTriangle } from 'lucide-react';

const StatsOverview = ({ devices = [], alerts = [] }) => {
  // Calculate statistics
  const totalDevices = devices.length;
  const onlineDevices = devices.filter(device => {
    // Consider device online if last reading was within 30 seconds
    const lastReading = new Date(device.timestamp);
    const now = new Date();
    return (now - lastReading) < 30000;
  }).length;

  // Check recent alerts (expand time window to 24 hours for active alerts)
  const activeAlerts = alerts.filter(alert => {
    // Handle different timestamp formats
    let alertTime;
    try {
      alertTime = new Date(alert.timestamp);
      // If timestamp has 'Z' or '+00:00', it's UTC
      if (alert.timestamp.includes('Z') || alert.timestamp.includes('+')) {
        alertTime = new Date(alert.timestamp);
      } else {
        // If no timezone info, assume it's local time
        alertTime = new Date(alert.timestamp);
      }
    } catch (e) {
      console.error('Invalid timestamp:', alert.timestamp);
      return false;
    }

    const now = new Date();
    const timeDiff = now - alertTime;

    // Debug: Log the time calculation for first few alerts
    if (alerts.indexOf(alert) < 3) {
      console.log(`Alert ${alert.device_name}: timestamp=${alert.timestamp}, parsed=${alertTime}, diff=${Math.floor(timeDiff/60000)}min`);
    }

    // Consider alert active if it's within the last 24 hours
    // Fix timezone issue: use absolute value for comparison
    const absTimeDiff = Math.abs(timeDiff);
    return absTimeDiff < 86400000 && !isNaN(timeDiff);
  }).length;

  // Show sample alert timestamps for debugging
  if (alerts.length > 0) {
    console.log('🚨 Alert age analysis:');
    alerts.slice(0, 5).forEach((alert, i) => {
      const alertTime = new Date(alert.timestamp);
      const now = new Date();
      const minutesAgo = Math.abs(Math.floor((now - alertTime) / 60000));
      console.log(`  Alert ${i+1}: ${alert.device_name} - ${alert.alert_type} - ${minutesAgo} minutes ago`);
    });

    // Find the most recent alert
    const mostRecent = alerts.reduce((newest, alert) => {
      return new Date(alert.timestamp) > new Date(newest.timestamp) ? alert : newest;
    });
    const recentMinutes = Math.floor((new Date() - new Date(mostRecent.timestamp)) / 60000);
    console.log(`📍 Most recent alert: ${recentMinutes} minutes ago`);
  }

  console.log(`📊 Alert Stats: Total=${alerts.length}, Active=${activeAlerts}`);

  // Calculate average values from all devices
  const avgCurrent = devices.length > 0 
    ? devices.reduce((sum, device) => sum + (device.current_amps || device.sensors?.current_amps || 0), 0) / devices.length
    : 0;

  const avgTemperature = devices.length > 0
    ? devices.reduce((sum, device) => sum + (device.temperature_celsius || device.sensors?.temperature_celsius || 0), 0) / devices.length
    : 0;

  const avgPressure = devices.length > 0
    ? devices.reduce((sum, device) => sum + (device.pressure_bar || device.sensors?.pressure_bar || 0), 0) / devices.length
    : 0;

  const StatCard = ({ title, value, unit, icon: Icon, color, bgColor }) => (
    <div className="bg-white rounded-lg shadow-md p-4">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm text-gray-600 mb-1">{title}</p>
          <p className="text-2xl font-bold text-gray-800">
            {value}
            {unit && <span className="text-lg text-gray-500 ml-1">{unit}</span>}
          </p>
        </div>
        <div className={`p-3 rounded-full ${bgColor}`}>
          <Icon className={`w-6 h-6 ${color}`} />
        </div>
      </div>
    </div>
  );

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-6 gap-4 mb-6">
      <StatCard
        title="Total Devices"
        value={totalDevices}
        icon={Activity}
        color="text-blue-600"
        bgColor="bg-blue-100"
      />

      <StatCard
        title="Online Devices"
        value={onlineDevices}
        icon={Activity}
        color="text-green-600"
        bgColor="bg-green-100"
      />

      <StatCard
        title="Active Alerts"
        value={activeAlerts}
        icon={AlertTriangle}
        color="text-red-600"
        bgColor="bg-red-100"
      />

      <StatCard
        title="Avg Current"
        value={avgCurrent.toFixed(1)}
        unit="A"
        icon={Activity}
        color="text-blue-600"
        bgColor="bg-blue-100"
      />

      <StatCard
        title="Avg Temperature"
        value={avgTemperature.toFixed(1)}
        unit="°C"
        icon={Thermometer}
        color="text-orange-600"
        bgColor="bg-orange-100"
      />

      <StatCard
        title="Avg Pressure"
        value={avgPressure.toFixed(1)}
        unit="bar"
        icon={Gauge}
        color="text-purple-600"
        bgColor="bg-purple-100"
      />
    </div>
  );
};

export default StatsOverview;
