import React, { useState, useEffect } from 'react';
import { supabase, TABLES } from './config/supabase';
import StatsOverview from './components/StatsOverview';
import SensorChart from './components/SensorChart';
import DeviceCard from './components/DeviceCard';
import AlertPanel from './components/AlertPanel';
import { RefreshCw, Zap } from 'lucide-react';

function App() {
  const [devices, setDevices] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [lastUpdate, setLastUpdate] = useState(null);
  const [error, setError] = useState(null);

  // Generate simulated device data for testing
  const generateSimulatedData = () => {
    const simulatedDevices = [];
    for (let i = 1; i <= 10; i++) {
      simulatedDevices.push({
        device_id: i,
        device_name: `EnergyDevice_${i.toString().padStart(3, '0')}`,
        timestamp: new Date().toISOString(),
        current_amps: (Math.random() * 15 + 5).toFixed(2),
        temperature_celsius: (Math.random() * 70 + 20).toFixed(2),
        pressure_bar: (Math.random() * 2 + 1).toFixed(2),
        sensors: {
          current_amps: parseFloat((Math.random() * 15 + 5).toFixed(2)),
          temperature_celsius: parseFloat((Math.random() * 70 + 20).toFixed(2)),
          pressure_bar: parseFloat((Math.random() * 2 + 1).toFixed(2))
        }
      });
    }
    return simulatedDevices;
  };

  // Fetch latest sensor readings for all devices
  const fetchDeviceData = async () => {
    try {
      setError(null);

      // Use simulated data for now
      const simulatedData = generateSimulatedData();
      setDevices(simulatedData);
      setLastUpdate(new Date());

      // TODO: Replace with real Supabase data once library is fixed
      /*
      const { data: sensorData, error: sensorError } = await supabase
        .from(TABLES.SENSOR_READINGS)
        .select('*')
        .order('timestamp', { ascending: false })
        .limit(500);
      */

    } catch (err) {
      console.error('Error in fetchDeviceData:', err);
      setError('Failed to connect to database');
    }
  };

  // Generate simulated alerts
  const generateSimulatedAlerts = () => {
    const alertTypes = ['HIGH_CURRENT', 'HIGH_TEMPERATURE', 'HIGH_PRESSURE'];
    const severities = ['warning', 'critical'];
    const simulatedAlerts = [];

    for (let i = 0; i < 5; i++) {
      const alertType = alertTypes[Math.floor(Math.random() * alertTypes.length)];
      const severity = severities[Math.floor(Math.random() * severities.length)];

      simulatedAlerts.push({
        device_name: `EnergyDevice_${Math.floor(Math.random() * 100 + 1).toString().padStart(3, '0')}`,
        alert_type: alertType,
        message: `${alertType.replace('_', ' ').toLowerCase()} detected`,
        severity: severity,
        sensor_value: Math.random() * 100,
        timestamp: new Date(Date.now() - Math.random() * 3600000).toISOString()
      });
    }

    return simulatedAlerts;
  };

  // Fetch recent alerts
  const fetchAlerts = async () => {
    try {
      // Use simulated alerts for now
      const simulatedAlerts = generateSimulatedAlerts();
      setAlerts(simulatedAlerts);

      // TODO: Replace with real Supabase data once library is fixed
      /*
      const { data: alertData, error: alertError } = await supabase
        .from(TABLES.ALERTS)
        .select('*')
        .order('timestamp', { ascending: false })
        .limit(50);
      */
    } catch (err) {
      console.error('Error in fetchAlerts:', err);
    }
  };

  // Initial data fetch
  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      await Promise.all([fetchDeviceData(), fetchAlerts()]);
      setLoading(false);
    };

    loadData();
  }, []);

  // Set up real-time updates every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      fetchDeviceData();
      fetchAlerts();
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  // Manual refresh
  const handleRefresh = async () => {
    setLoading(true);
    await Promise.all([fetchDeviceData(), fetchAlerts()]);
    setLoading(false);
  };

  if (loading && devices.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <RefreshCw className="w-8 h-8 text-blue-500 animate-spin mx-auto mb-4" />
          <p className="text-gray-600">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-3">
              <Zap className="w-8 h-8 text-blue-600" />
              <div>
                <h1 className="text-xl font-bold text-gray-900">
                  IoT Energy Management
                </h1>
                <p className="text-sm text-gray-500">Real-time monitoring dashboard</p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              {lastUpdate && (
                <span className="text-sm text-gray-500">
                  Last update: {lastUpdate.toLocaleTimeString()}
                </span>
              )}
              <button
                onClick={handleRefresh}
                disabled={loading}
                className="flex items-center space-x-2 px-3 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
              >
                <RefreshCw className={`w-4 h-4 ${loading ? 'animate-spin' : ''}`} />
                <span>Refresh</span>
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        {error && (
          <div className="mb-6 bg-red-50 border border-red-200 rounded-lg p-4">
            <p className="text-red-800">{error}</p>
          </div>
        )}

        {/* Stats Overview */}
        <StatsOverview devices={devices} alerts={alerts} />

        {/* Charts Section */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-6">
          <SensorChart
            data={devices}
            title="Current Consumption"
            dataKey="current_amps"
            color="#3b82f6"
            unit="A"
            threshold={15}
          />
          <SensorChart
            data={devices}
            title="Temperature"
            dataKey="temperature_celsius"
            color="#f59e0b"
            unit="Â°C"
            threshold={80}
          />
          <SensorChart
            data={devices}
            title="Pressure"
            dataKey="pressure_bar"
            color="#8b5cf6"
            unit="bar"
            threshold={2.5}
          />
        </div>

        {/* Devices and Alerts */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Device Grid */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-lg shadow-md p-4 mb-6">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">
                Device Status ({devices.length} devices)
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-4 max-h-96 overflow-y-auto">
                {devices.map((device) => (
                  <DeviceCard
                    key={device.device_id}
                    device={device}
                    isOnline={true}
                  />
                ))}
              </div>
            </div>
          </div>

          {/* Alerts Panel */}
          <div className="lg:col-span-1">
            <AlertPanel alerts={alerts} />
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
